import obdimport timedevices = obd.scan_serial()lastValues = {}try:    connection = obd.OBD(devices[0])    pids = connection.supported_commands    print(str(devices[0]))except Exception as ELM_missing:    print("Chyba pripojenia k vozidlu: " + str(ELM_missing))    exit()while True:    loopCounter=0    for pid in pids:        try:            loopCounter = loopCounter + 1            if loopCounter == 1:                lastValues[pid.name] = 0            else:                decrypted_pid = connection.query(obd.commands[pid.name])                if decrypted_pid.value != lastValues[pid.name]:                    print(str(decrypted_pid.value) + " " + str(pid.name))                lastValues[pid.name] = decrypted_pid.value        except Exception as Decryption_failed:            print("Chyba dekodovania hodnoty: " + str(pid.name) + " ,chyba: " + str(Decryption_failed))    time.sleep(5)