import obdimport timedevices = obd.scan_serial()lastValues = {}try:    connection = obd.OBD(devices[0])    pids = connection.supported_commandsexcept Exception as ELM_missing:    print("Chyba pripojenia k vozidlu: " + str(ELM_missing))    exit()while True:    for pid in pids:        try:            decrypted_pid = connection.query(obd.commands[pid.name])            if str(decrypted_pid.value) != lastValues[str(pid.name)]:                print(str(decrypted_pid.value) + " " + str(pid.name))            lastValues[str(pid.name)] = str(decrypted_pid.value)        except Exception as Decryption_failed:            print("Chyba dekodovania hodnoty: " + str(pid.name) + " ,chyba: " + str(Decryption_failed))    time.sleep(5)